mapper.select:
SELECT COUNT(*) 
FROM USER 
WHERE BINARY USERNAME LIKE #{prefix}

SELECT USERNAME, NAME
  FROM USER
 WHERE BINARY USERNAME LIKE #{prefix}

SELECT * 
  FROM USER
 WHERE PHONE=#{phone}
   AND USERNAME=#{username}

SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES
WHERE TABLE_SCHEMA = 'final'
AND TABLE_NAME = 'USER';

SELECT * FROM DOCUMENT D INNER JOIN FORM F ON D.FORM_ID=F.FORM_ID
D.WRITER_ID = #{writerId}
AND D.DOCUMENT_TITLE LIKE CONCAT('%', #{pager.searchWord}, '%')
AND F.FORM_TITLE LIKE CONCAT('%', #{pager.searchWord}, '%')
AND D.DOCUMENT_STATUS = 'D0' AND D.DOCUMENT_STATUS = 'D1' AND D.DOCUMENT_STATUS = 'D2'
ORDER BY D.CREATED_AT DESC LIMIT #{pager.startRow}, #{pager.pageSize}

SELECT *
FROM DOCUMENT D
INNER JOIN FORM F
ON D.FORM_ID=F.FORM_ID
WHERE D.DOCUMENT_ID=#{documentId}
AND D.WRITER_ID=#{writerId}
